// breakpoints

$S:     24em;   
$M:     46.8em;   
$L:     50em;  
$XL:    73em; 

// media queries

@mixin MQ($canvas) {
  @if $canvas == S {
   @media only screen and (min-width: $S) { @content; } 
  }
  @else if $canvas == M {
   @media only screen and (min-width: $M) { @content; } 
  }

  @else if $canvas == L {
   @media only screen and (min-width: $L) { @content; } 
  }

  @else if $canvas == XL {
    @media only screen and (min-width: $XL) { @content; } 
   }
}

